import React, { useState } from "react";

const COMPONENTS = [
  { key: "run", label: "1.5 Mile Run (seconds)", type: "number" },
  { key: "pushups", label: "Push-ups (reps)", type: "number" },
  { key: "situps", label: "Sit-ups (reps)", type: "number" },
];

function App() {
  const [gender, setGender] = useState("male");
  const [age, setAge] = useState(25);
  const [values, setValues] = useState({ run: "", pushups: "", situps: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    // Only include filled components
    const components = {};
    Object.entries(values).forEach(([k, v]) => {
      if (v !== "") components[k] = Number(v);
    });
    try {
      const res = await fetch("/api/pt/calculate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ gender, age: Number(age), components }),
      });
      const data = await res.json();
      if (res.ok) setResult(data);
      else setError(data.message || "Error");
    } catch (err) {
      setError("Network error");
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "2rem auto", padding: 20, fontFamily: "sans-serif" }}>
      <h2>USAF PT Calculator</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Gender:
          <select value={gender} onChange={e => setGender(e.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <br />
        <label>
          Age:
          <input type="number" value={age} min={17} max={100} onChange={e => setAge(e.target.value)} required />
        </label>
        <br />
        {COMPONENTS.map(c => (
          <label key={c.key} style={{ display: "block", marginTop: 10 }}>
            {c.label}:
            <input
              type={c.type}
              name={c.key}
              value={values[c.key]}
              onChange={handleChange}
              min={0}
              required
            />
          </label>
        ))}
        <br />
        <button type="submit">Calculate</button>
      </form>
      {result && (
        <div style={{ marginTop: 20 }}>
          <h3>Results</h3>
          {Object.entries(result.component_scores).map(([comp, score]) => (
            <div key={comp} style={{ marginBottom: 8 }}>
              <b>{comp.charAt(0).toUpperCase() + comp.slice(1)}:</b> {score.score} (Min: {score.min_score}, Max: {score.max_score})
              {score.message && <span style={{ color: "orange" }}> â€“ {score.message}</span>}
            </div>
          ))}
          <div style={{ marginTop: 10 }}>
            <b>Total Score:</b> {result.total_score}
          </div>
        </div>
      )}
      {error && <div style={{ color: "red", marginTop: 20 }}>{error}</div>}
    </div>
  );
}

export default App;
