import React, { useState, useEffect } from "react";

const COMPONENTS = [
  { key: "run", label: "1.5 Mile Run (mm:ss)", type: "time" },
  { key: "pushups", label: "Push-ups (reps)", type: "number" },
  { key: "situps", label: "Sit-ups (reps)", type: "number" },
];

function mmssToSeconds(mmss) {
  // Accepts 'mm:ss' or 'm:ss' or 'ss' or 'mss' or 'mmss' (auto-formats)
  if (!mmss) return 0;
  let clean = mmss.replace(/[^0-9]/g, "");
  if (clean.length <= 2) return parseInt(clean, 10) || 0;
  if (clean.length === 3) clean = '0' + clean; // e.g. 456 -> 0456
  let min = parseInt(clean.slice(0, -2), 10);
  let sec = parseInt(clean.slice(-2), 10);
  return min * 60 + sec;
}

function autoFormatMMSS(val) {
  // Auto-format to mm:ss as user types
  let clean = val.replace(/[^0-9]/g, "");
  if (clean.length === 0) return "";
  if (clean.length <= 2) return clean;
  if (clean.length === 3) return clean[0] + ":" + clean.slice(1); // e.g. 934 -> 9:34
  if (clean.length === 4) return clean.slice(0,2) + ":" + clean.slice(2); // e.g. 1024 -> 10:24
  // For longer input, just use last 4 digits
  if (clean.length > 4) return clean.slice(-4, -2) + ":" + clean.slice(-2);
  return clean;
}

function formatMMSS(seconds) {
  if (typeof seconds !== "number" || isNaN(seconds)) return "--:--";
  const m = Math.floor(seconds / 60);
  const s = seconds % 60;
  return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
}

const styles = {
  container: {
    maxWidth: 480,
    margin: "2rem auto",
    padding: 24,
    borderRadius: 16,
    background: "#f8fafc",
    boxShadow: "0 2px 16px 0 rgba(0,0,0,0.08)",
    fontFamily: "system-ui, sans-serif",
  },
  heading: {
    color: "#003366",
    textAlign: "center",
    marginBottom: 24,
    fontWeight: 700,
    fontSize: 28,
    letterSpacing: 1,
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: 16,
  },
  label: {
    fontWeight: 500,
    marginBottom: 4,
    color: "#222",
  },
  input: {
    padding: 10,
    borderRadius: 8,
    border: "1px solid #b0b8c1",
    fontSize: 16,
    marginBottom: 8,
    width: "100%",
    boxSizing: "border-box",
  },
  button: {
    background: "#003366",
    color: "#fff",
    border: "none",
    borderRadius: 8,
    padding: "12px 0",
    fontWeight: 600,
    fontSize: 18,
    marginTop: 8,
    cursor: "pointer",
    transition: "background 0.2s",
  },
  card: {
    background: "#fff",
    borderRadius: 12,
    boxShadow: "0 1px 6px 0 rgba(0,0,0,0.06)",
    padding: 16,
    marginTop: 20,
  },
  resultLabel: {
    fontWeight: 600,
    color: "#003366",
    marginRight: 8,
  },
  error: {
    color: "#b00020",
    marginTop: 16,
    textAlign: "center",
  },
  '@media (maxWidth: 600px)': {
    container: { padding: 8, maxWidth: '98vw' },
    heading: { fontSize: 22 },
    button: { fontSize: 16 },
  },
};

function App() {
  const [gender, setGender] = useState("male");
  const [age, setAge] = useState(25);
  const [values, setValues] = useState({ run: "", pushups: "", situps: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [minMax, setMinMax] = useState({});

  // Fetch min/max when gender or age changes
  useEffect(() => {
    async function fetchMinMax() {
      try {
        const res = await fetch(`/api/pt/minmax?gender=${gender}&age=${age}`);
        const data = await res.json();
        setMinMax(data);
      } catch {
        setMinMax({});
      }
    }
    fetchMinMax();
  }, [gender, age]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    if (name === "run") {
      // Auto-format mm:ss
      setValues({ ...values, [name]: autoFormatMMSS(value) });
    } else {
      setValues({ ...values, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    const components = {};
    Object.entries(values).forEach(([k, v]) => {
      if (v !== "") {
        if (k === "run") components[k] = mmssToSeconds(v);
        else components[k] = Number(v);
      }
    });
    try {
      const res = await fetch("/api/pt/calculate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ gender, age: Number(age), components }),
      });
      const data = await res.json();
      if (res.ok) setResult(data);
      else setError(data.message || "Error");
    } catch (err) {
      setError("Network error");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.heading}>USAF PT Calculator</div>
      <form style={styles.form} onSubmit={handleSubmit} autoComplete="off">
        <label style={styles.label}>
          Gender
          <select style={styles.input} value={gender} onChange={e => setGender(e.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <label style={styles.label}>
          Age
          <input style={styles.input} type="number" value={age} min={17} max={100} onChange={e => setAge(e.target.value)} required />
        </label>
        {COMPONENTS.map(c => (
          <label key={c.key} style={styles.label}>
            {c.label}
            <input
              style={styles.input}
              type={c.type === "time" ? "text" : c.type}
              name={c.key}
              value={values[c.key]}
              onChange={handleChange}
              min={minMax[c.key]?.min ?? 0}
              max={minMax[c.key]?.max ?? undefined}
              placeholder={c.type === "time" ? "mm:ss" : undefined}
              required
            />
            <span style={{ fontSize: 13, color: '#555', marginLeft: 6 }}>
              {c.key === "run" && minMax[c.key]?.max !== undefined && (
                `Max: ${formatMMSS(minMax[c.key].max)}`
              )}
              {c.key !== "run" && minMax[c.key]?.min !== undefined && minMax[c.key]?.max !== undefined && (
                `Min: ${minMax[c.key].min}, Max: ${minMax[c.key].max}`
              )}
            </span>
          </label>
        ))}
        <button style={styles.button} type="submit">Calculate</button>
      </form>
      {result && (
        <div style={styles.card}>
          <h3 style={{marginTop:0, color:'#003366'}}>Results</h3>
          {Object.entries(result.component_scores).map(([comp, score]) => (
            <div key={comp} style={{ marginBottom: 8 }}>
              <span style={styles.resultLabel}>{comp.charAt(0).toUpperCase() + comp.slice(1)}:</span>
              {score.score} (Min: {score.min_score}, Max: {score.max_score})
              {score.message && <span style={{ color: "orange" }}> â€“ {score.message}</span>}
            </div>
          ))}
          <div style={{ marginTop: 10, fontWeight: 700 }}>
            <span style={styles.resultLabel}>Total Score:</span> {result.total_score}
          </div>
        </div>
      )}
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
}

export default App;
