import React, { useState } from "react";

const COMPONENTS = [
  { value: "run", label: "1.5 Mile Run (seconds)" },
  { value: "hamr", label: "HAMR (shuttles)" },
  { value: "pushups", label: "Push-ups (reps)" },
  { value: "hr_pushups", label: "Hand Release Push-ups (reps)" },
  { value: "situps", label: "Sit-ups (reps)" },
  { value: "crunch", label: "Crunch (reps)" },
  { value: "plank", label: "Plank (seconds)" },
];

function App() {
  const [gender, setGender] = useState("male");
  const [age, setAge] = useState(25);
  const [component, setComponent] = useState("run");
  const [value, setValue] = useState(0);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    try {
      const res = await fetch("/api/pt/calculate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ gender, age: Number(age), component, value: Number(value) }),
      });
      const data = await res.json();
      if (res.ok) setResult(data);
      else setError(data.message || "Error");
    } catch (err) {
      setError("Network error");
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "2rem auto", padding: 20, fontFamily: "sans-serif" }}>
      <h2>USAF PT Calculator</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Gender:
          <select value={gender} onChange={e => setGender(e.target.value)}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <br />
        <label>
          Age:
          <input type="number" value={age} min={17} max={100} onChange={e => setAge(e.target.value)} required />
        </label>
        <br />
        <label>
          Component:
          <select value={component} onChange={e => setComponent(e.target.value)}>
            {COMPONENTS.map(c => (
              <option key={c.value} value={c.value}>{c.label}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Value:
          <input type="number" value={value} onChange={e => setValue(e.target.value)} required />
        </label>
        <br />
        <button type="submit">Calculate</button>
      </form>
      {result && (
        <div style={{ marginTop: 20 }}>
          <h3>Result</h3>
          <div>Score: <b>{result.score}</b></div>
          <div>Max Score: {result.max_score}</div>
          <div>Min Score: {result.min_score}</div>
          {result.message && <div style={{ color: "orange" }}>{result.message}</div>}
        </div>
      )}
      {error && <div style={{ color: "red", marginTop: 20 }}>{error}</div>}
    </div>
  );
}

export default App;
